import {useEffect, useState} from 'react';

const useFetch = () => {

   const [data1, setData1] = useState({});
   const [loading, setLoading] = useState("true")
   const [error, setError] = useState("false")

    useEffect(() => {
        const fetchData = async () => {
            //setLoading("false")

            try {
              const responsesJSON = await Promise.all([
                  fetch('https://jsonplaceholder.typicode.com/todos/1'),
                  fetch('https://jsonplaceholder.typicode.com/todos/2')
              ]);
              const [todoOne, todoTwo] = await Promise.all(responsesJSON.map(r => r.json()));
              console.log(todoOne, 'todoOne');
              //setData1(todoOne) 
              //setData1(Object.assign({}, todoOne, todoTwo))
              setData1([todoOne, todoTwo])
              //ar food = Object.assign({}, a, b);
              console.log(todoTwo, 'todoOne');
              console.log(todoTwo, 'todoTwo');
              setLoading("false")

            } catch (err) {
              setError("true")
              throw err;
            }
          };

          fetchData();
    }, []);

    return {data1, loading, error}
}
