import {useEffect, useState} from 'react';
import useFetch from './useFetch.js'

const useObtainAuth = () => {

   const [loading, setLoading] = useState(true)
   const [token, setToken] = useState("")

   const url = "https://api.preqin.com/connect/token";

   //const newURLSearchParams = window.URLSearchParams;

    useEffect(() => {
        const fetchData = async () => {
            try {

                const response = await fetch(url, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/x-www-form-urlencoded"
                    },
                    body: new URLSearchParams({
                        "username": "dummydatafeeds@preqin.com",
                        "apikey": "8f0bc69bc2a643f8bb8034a15081962e"
                    })
                });
                const reply = await response.json();
                setToken(reply.access_token)
                console.log(token);
                //setLoading(false)

            } catch (error) {
                console.log("error:", error);
            }
           };

          fetchData();
    }, [token]);

    return {token, loading, error}
}

export default useObtainAuth;
